function getBurnTransactionInfo(address, vin = [], vouts = []) {
  let lockerVin = vin.find((vi) => vi.address === address)
  if (lockerVin) {
    let totalInputValue = vin.reduce((acc, current) => +acc + +current.value, 0)
    let receivers = []
    let changes = []
    for (let i in vouts) {
      let vout = {
        ...vouts[i],
        index: i,
      }
      if (vout.address === address) {
        changes.push(vout)
      } else {
        receivers.push(vout)
      }
    }
    return { receivers, changes, totalInputValue, lockerVin }
  }
  return null
}

module.exports = {
  getBurnTransactionInfo,
}
